definition.script:
node 
{

   withCredentials([[
        $class: 'UsernamePasswordMultiBinding',
        credentialsId: 'sonarqube_cred_id',
        usernameVariable: 'username',
        passwordVariable: 'password']]) {
        BD_USER = "${env.username}"
        BD_PASSWORD = "${env.password}"
        }

        echo "userid: $BD_USER"
        echo "pwd: $BD_PASSWORD"
}
