definition.script:
node
{
echo "${env.JOB_NAME}"
withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'sonarqube_cred_id', usernameVariable: 'sonar_jdbc_user', passwordVariable: 'sonar_jdbc_password']]) {
withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'docker_creds_id',usernameVariable: 'USERNAME',passwordVariable: 'PASSWORD']]) {
echo "${env.USERNAME} ${env.PASSWORD}"
echo "${env.sonar_jdbc_user} ${env.sonar_jdbc_password}"
}
echo "${env.sonar_jdbc_user} ${env.sonar_jdbc_password}"
}
String jobName = "${env.JOB_NAME}"
if( jobName == "tests" )
{
echo "$jobName"  
}
}
